mapper.select:
SELECT 

FROM tpunish_lib a

WHERE a.id = #{id}

SELECT 

FROM tpunish_lib a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.seq = #{seq}


		AND a.behavior = #{behavior}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tpunish_lib a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tpunish_lib SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
