mapper.select:
SELECT 

FROM tsignature a

WHERE a.id = #{id}

SELECT 

FROM tsignature a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tsignature a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tsignature SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
