mapper.select:
SELECT 

FROM tsignature_lib a

WHERE u.login_name = #{user.loginName}

SELECT u.login_name AS "user.loginName", u.name AS "user.name", a.title AS "title", a.signature AS "signature" FROM sys_user u LEFT JOIN tsignature_lib a on a.login_name=u.login_name
u.del_flag = #{DEL_FLAG_NORMAL} AND u.user_type='1'
AND u.login_name = #{user.loginName}
AND u.name like concat('%',#{user.name},'%')
ORDER BY ${page.orderBy}
ORDER BY u.login_name

SELECT 

FROM tsignature_lib a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tsignature_lib SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE login_name = #{user.loginName}
