mapper.select:
SELECT 

FROM tquestionanswer_item a

WHERE a.id = #{id}

SELECT 

FROM tquestionanswer_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.qa_id = #{qaId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM tquestionanswer_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE tquestionanswer_item SET 	
	qa_content = #{qaContent},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE tquestionanswer_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE qa_id = #{qaId}
