mapper.select:
SELECT 

FROM tcase_handle a

WHERE case_id = #{caseId}

SELECT 

FROM tcase_handle a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.case_id = #{caseId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tcase_handle a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tcase_handle SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
