mapper.select:
SELECT 

FROM tcase a

WHERE a.id = #{id}

SELECT 

FROM tcase a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.accept_date = #{acceptDate}


		AND a.org_name = #{orgName}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tcase a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tcase SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
